function shuffle(t){for(var e=t.length,n,o;0!==e;)o=Math.floor(Math.random()*e),e-=1,n=t[e],t[e]=t[o],t[o]=n;return t}function timeout(t,e){e>=t.length&&(shuffle(t),e=0),setTimeout(function(){var n=document.getElementById(t[e]);n.classList.toggle("flipped");var o=document.getElementById("n"+t[e]);o.classList.toggle("flipped"),timeout(t,e+1)},2e3)}window.onload=function(){var t=["c0","c1","c2","c3","c4","c5","c6"];shuffle(t),timeout(t,0)};